  mystring  = paste("https://kuybs3qucnn2f6djohgb2cscbm0bppme.lambda-url.eu-west-2.on.aws/iiif/2/designarchives%2F",filenamewithoutplus,"/full/90,/0/color.jpg", sep = "")
