  # print(min(somevaluesy))
  # print(max(somevaluesx))
  # print(mean(somevaluesx))
  # print(mean(somevaluesy))
  # print(sd(somevaluesx))
  # print(sd(somevaluesy))
  #data$x = somevaluesx
  # data$x = sin(somevaluesx)*cos(somevaluesy)
  # data$y = sin(somevaluesx)*sin(somevaluesy)
  # data$z = cos(somevaluesx)
  # data$x = sin(data$x)*cos(data$y)
  # data$y = sin(data$x)*sin(data$y)
  # data$z = cos(data$x)
  # data$x = somevaluesx
  # data$y = somevaluesy
  # # data$z = 0
  data$x = data$x
  data$y = data$y
  data$z = data$z

  # data$x = sin(data$x)*cos(data$y)
  # data$y = sin(data$x)*sin(data$y)
  # data$z = cos(data$y)
  #https://stackoverflow.com/questions/9604132/how-to-project-a-point-on-to-a-sphere
  centremeshx = mean(data$x)
  centremeshy = mean(data$y)
  centremeshz = mean(data$z)
  # print(centremeshx)
  # print(centremeshy)
  # print(centremeshz)

  vectorprojectedx = data$x - centremeshx
  vectorprojectedy = data$y - centremeshy
  vectorprojectedz = data$z - centremeshz

  lengthofvector = sqrt(vectorprojectedx^2 + vectorprojectedy^2 + vectorprojectedz^2)
  radius = 1
  # unitvector = vectorprojectedx/lengthofvector
  vectorprojectedxscaled = (vectorprojectedx/lengthofvector)
  vectorprojectedyscaled = (vectorprojectedy/lengthofvector)
  vectorprojectedzscaled = (vectorprojectedz/lengthofvector)
  lengthofvectorscaled = (vectorprojectedxscaled^2 + vectorprojectedyscaled^2 + vectorprojectedzscaled^2)
  # print("lengthofvectorscaled")
  # print(lengthofvectorscaled)
  data$xsphere = 0 + vectorprojectedxscaled
  data$ysphere = 0 + vectorprojectedyscaled
  data$zsphere = 0 + vectorprojectedzscaled


  # print(unique(data$predicted1));
